import React, { Component, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import Form from './component/Form';


const API_ID = "3abdb491";
const API_KEY = "261ee1b36395466731dabdd480bb885a";

class App extends Component {
  state = {
    recipes: []
  }


  getReceipe = async (e) => {
    const receipeName = e.target.elements.receipeName.value;
    e.preventDefault();
    console.log(receipeName);
    const api_call = await fetch(`https://api.edamam.com/search?q=${receipeName}&app_id=${API_ID}&app_key=${API_KEY}`);
    const data = await api_call.json();
    this.setState({
      recipes: data.hits
    });
    console.log(this.state.recipes);
  }
  render() {
    return (
      <div className="App" >
        <header className="App-header">
          <h1 className="App-title">Receipe-Application</h1>
        </header>
        <Form getReceipe={this.getReceipe} />
      </div>
    );
  }
}

export default App;
