import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Form from './component/Form';


const API_ID = "3abdb491";

const API_KEY = "261ee1b36395466731dabdd480bb885a";

class App extends Component {
  state = {
    movie: []
  }


  getReceipe = async (e) => {
    const movieName = e.target.elements.receipeName.value;
    e.preventDefault();

    const api_call = await fetch(`http://www.omdbapi.com/?apikey=e5004a7b&s=${movieName}`);
    const data = await api_call.json();
    this.setState({
      movie: data
    });
    console.log(this.state.movie);
  }


  render() {
    const { movie } = this.state;
    return (
      <div className="App" >
        <header className="App-header">
          <h1 className="App-title">Movies Application</h1>
        </header>
        <Form getReceipe={this.getReceipe} />
        {movie.map(movie => { <p>{movie.Title}</p> })}
      </div>
    );
  }
}

export default App;
