import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Form from './component/Form'
class App extends Component {
  getReceipe = async (e) => {
    const receipeName = e.target.elements.receipeName.value;
    e.preventDefault();
    console.log(receipeName);
    const api_call = await fetch("https://spoonacular-recipe-food-nutrition-v1.p.rapidapi.com/recipes/479101/information", {
      "method": "GET",
      "headers": {
        "x-rapidapi-host": "spoonacular-recipe-food-nutrition-v1.p.rapidapi.com",
        "x-rapidapi-key": "1a993d0ba5msh124a041e64b17c1p19e740jsnf8eb9cd674c1"
      }
    });
    const data = await api_call.json();
    console.log(data);

  }
  render() {
    return (
      <div className="App" >
        <header className="App-header">
          <h1 className="App-title">Receipe-Application</h1>
        </header>
        <Form getReceipe={this.getReceipe} />
      </div>
    );
  }
}

export default App;
